import React from 'react';
import File from './File.js';
import { useEffect, useState } from 'react';
import vik from "./vikrant.png"
function Files() {
    const [ntp, setntp] = useState([]);
    useEffect(() => {
        fetch("http://localhost:7654/FILES")
            .then((response) => response.json())
            .then((data) => setntp(data));
    }, []);
    const filteredFiles = ntp.filter((file) => {
        return file.includes(searchValue);
    })
    function load() {
            if (searchValue == "") {
                return ntp.map(file => <File name={file.name} url={file.url} />)
            }
            else {
                    return filteredFiles.map(file => <File name={file.name} url={file.url} />)
            }

        }
    const [searchValue, setSearchValue] = useState("")
    const handleInputChange = (event) => {
        setSearchValue(event.target.value)
    }
    return (
        <div>
            <img src={vik} />
            <div className='tasks-list-container'>
                <div className='form'>
                    <form>
                        <input className='input' value={searchValue} onChange={handleInputChange} placeholder='ðŸ” Search Files' />
                    </form>
                </div>
                <div className='tasks-list'>
                    {load()}
                </div>
            </div>
        </div>
    );
}

export default Files;
